/*\
title: $:/plugins/NiCo/filesaver/disable-default-save
type: application/javascript
module-type: startup

Disable TiddlyWiki's default save shortcuts when FSA is available

\*/
(function(){

/*jslint node: true, browser: true */
/*global $tw: false */
"use strict";

exports.name = "disable-default-save";
exports.platforms = ["browser"];
exports.after = ["startup"];
exports.synchronous = true;

exports.startup = function() {
    if($tw.browser && 'showSaveFilePicker' in window) {
        // Disable any existing keyboard shortcuts for saving
        setTimeout(function() {
            var rootWidget = $tw.rootWidget;
            if (rootWidget && rootWidget.children) {
                // Find and disable save shortcuts
                $tw.utils.each(rootWidget.children, function(child) {
                    if (child.shortcuts) {
                        delete child.shortcuts["ctrl+s"];
                        delete child.shortcuts["meta+s"];
                    }
                });
            }
        }, 100);
    }
};

})();

/*\
title: $:/plugins/NiCo/filesaver/filesaver.js
type: application/javascript
module-type: startup

File System Access API Saver for TiddlyWiki

\*/
(function(){

/*jslint node: true, browser: true */
/*global $tw: false */
"use strict";

// Export name and synchronous status
exports.name = "filesaver";
exports.platforms = ["browser"];
exports.after = ["startup"];
exports.synchronous = true;

var fileHandle = null;
var isProcessingSave = false;

// Check if File System Access API is supported
function isFileSystemAccessSupported() {
    return typeof window !== 'undefined' && 
           'showSaveFilePicker' in window &&
           'showOpenFilePicker' in window &&
           'showDirectoryPicker' in window;
}

// Check if we're running from a local file
function isLoadedFromFile() {
    return window.location.protocol === 'file:';
}

// Get suggested filename from current URL or default
function getSuggestedFilename() {
    if (isLoadedFromFile()) {
        const path = window.location.pathname;
        const filename = path.split('/').pop();
        if (filename && filename.endsWith('.html')) {
            return filename;
        }
    }
    return 'tiddlywiki.html';
}

// Show toast notification
function showToast(message, isSuccess, duration = 3000) {
    var toast = document.createElement('div');
    toast.style.cssText = `
        position: fixed;
        bottom: 20px;
        right: 20px;
        padding: 12px 20px;
        border-radius: 4px;
        color: white;
        font-family: -apple-system, BlinkMacSystemFont, "Segoe UI", sans-serif;
        font-size: 14px;
        z-index: 10000;
        opacity: 0;
        transition: opacity 0.3s ease;
        max-width: 350px;
        word-wrap: break-word;
        ${isSuccess ? 'background-color: #4CAF50;' : 'background-color: #f44336;'}
    `;
    toast.textContent = message;

    document.body.appendChild(toast);

    // Fade in
    setTimeout(() => toast.style.opacity = '1', 10);

    // Remove after specified duration
    setTimeout(() => {
        toast.style.opacity = '0';
        setTimeout(() => {
            if (toast.parentNode) {
                document.body.removeChild(toast);
            }
        }, 300);
    }, duration);
}

// Get file handle - either by opening current file or saving new one
async function getFileHandle() {
    if (isLoadedFromFile()) {
        // We're running from a local file, ask user to select it for writing
        console.log('Loaded from local file, asking user to select current file');

        const options = {
            types: [{
                description: 'TiddlyWiki files',
                accept: {
                    'text/html': ['.html']
                }
            }],
            multiple: false
        };

        const [handle] = await window.showOpenFilePicker(options);
        console.log('User selected file:', handle.name);

        return handle;
    } else {
        // We're running from web, use save picker
        console.log('Running from web, using save picker');

        const options = {
            types: [{
                description: 'TiddlyWiki files',
                accept: {
                    'text/html': ['.html']
                }
            }],
            suggestedName: getSuggestedFilename()
        };

        return await window.showSaveFilePicker(options);
    }
}

// Select/change the target file
async function selectFile() {
    try {
        console.log('Opening file selection dialog');
        const newHandle = await getFileHandle();
        fileHandle = newHandle;

        // Update button text
        updateButtonText();

        showToast(`File selected: ${fileHandle.name}`, true);
        console.log('File handle updated:', fileHandle.name);

    } catch (error) {
        if (error.name === 'AbortError') {
            console.log('File selection cancelled by user');
        } else {
            console.error('File selection failed:', error);
            showToast('File selection failed: ' + error.message, false);
        }
    }
}

// Save using TiddlyWiki's mechanism but with our file handle
async function saveFileWithFSA() {
    if (isProcessingSave) {
        console.log('Save already in progress, ignoring duplicate request');
        return;
    }

    // If no file is selected, select one first
    if (!fileHandle) {
        showToast('No file selected. Please choose a file first.', false);
        await selectFile();
        if (!fileHandle) {
            return; // User cancelled file selection
        }
    }

    isProcessingSave = true;
    console.log('Starting File System Access API save through TiddlyWiki mechanism');

    try {
        // Create a custom saver that uses our file handle
        const customSaver = {
            info: {
                name: "file-system-access",
                priority: 5000,
                capabilities: ["save", "autosave"]
            },

            save: async function(text, method, callback) {
                try {
                    console.log('Custom saver called, writing to file handle:', fileHandle.name);

                    // Write using our file handle
                    const writable = await fileHandle.createWritable();
                    await writable.write(text);
                    await writable.close();

                    console.log('File written successfully');

                    // Call callback with success
                    if (callback) {
                        callback(null);
                    }

                    showToast(`File saved: ${fileHandle.name}`, true);
                    return true;

                } catch (error) {
                    console.error('Custom saver failed:', error);
                    if (callback) {
                        callback(error);
                    }

                    if (error.name === 'NotAllowedError') {
                        showToast('File access denied. Please select the file again.', false);
                        fileHandle = null;
                        updateButtonText();
                    } else {
                        showToast('Save failed: ' + error.message, false);
                    }
                    return false;
                }
            }
        };

        // Temporarily replace the browser saver
        let originalSavers = [];
        if ($tw.saverHandler && $tw.saverHandler.savers) {
            originalSavers = [...$tw.saverHandler.savers];
            $tw.saverHandler.savers = [customSaver];
        }

        // Trigger TiddlyWiki's built-in save
        console.log('Triggering TiddlyWiki save...');
        if ($tw.rootWidget) {
            $tw.rootWidget.dispatchEvent({
                type: "tm-save-wiki",
                param: "",
                paramObject: {}
            });
        }

        // Restore original savers after a delay
        setTimeout(() => {
            if ($tw.saverHandler && originalSavers.length > 0) {
                $tw.saverHandler.savers = originalSavers;
                console.log('Original savers restored');
            }
        }, 1000);

    } catch (error) {
        console.error('Save failed:', error);
        showToast('Save failed: ' + error.message, false);
    } finally {
        isProcessingSave = false;
        console.log('Save process completed');
    }
}

// Handle Ctrl+S keyboard shortcut
function handleKeyDown(event) {
    // Check for Ctrl+S (or Cmd+S on Mac)
    if ((event.ctrlKey || event.metaKey) && event.key === 's') {
        console.log('Ctrl+S detected');

        if (isFileSystemAccessSupported()) {
            console.log('FSA supported, preventing default and using custom save');

            // Prevent default behavior
            event.preventDefault();
            event.stopPropagation();
            event.stopImmediatePropagation();

            // Call our save function
            saveFileWithFSA();

            return false;
        } else {
            console.log('FSA not supported');
            showToast('File System Access API not supported', false);
        }
    }
}

// Update button text based on current state
function updateButtonText() {
    const button = document.getElementById('fsa-file-selector');
    if (button) {
        if (fileHandle) {
            // Show truncated filename if too long
            let displayName = fileHandle.name;
            if (displayName.length > 15) {
                displayName = displayName.substring(0, 12) + '...';
            }
            button.innerHTML = `üìÅ ${displayName}`;
            button.title = `Currently saving to: ${fileHandle.name}\nClick to change file`;
        } else {
            button.innerHTML = 'üìÅ Select File';
            button.title = 'Click to select a file for Ctrl+S saving';
        }
    }
}

// Add file selector button with better TiddlyWiki integration
function addFileSelectorButton() {
    if (!document.getElementById('fsa-file-selector')) {
        const button = document.createElement('button');
        button.id = 'fsa-file-selector';
        button.innerHTML = 'üìÅ Select File';
        button.title = 'Click to select a file for Ctrl+S saving';

        // Style to match TiddlyWiki's aesthetic
        button.style.cssText = `
            position: fixed;
            bottom: 15px;
            left: 15px;
            z-index: 900;
            padding: 8px 12px;
            background: #5778d8;
            color: white;
            border: 1px solid #4567cd;
            border-radius: 3px;
            cursor: pointer;
            font-size: 11px;
            font-family: "Helvetica Neue", Helvetica, Arial, "Lucida Grande", sans-serif;
            box-shadow: 0 1px 4px rgba(0,0,0,0.3);
            transition: all 0.2s ease;
            min-width: 100px;
            text-align: left;
        `;

        // Hover effects
        button.addEventListener('mouseenter', () => {
            button.style.background = '#4567cd';
            button.style.transform = 'translateY(-1px)';
            button.style.boxShadow = '0 2px 6px rgba(0,0,0,0.4)';
        });

        button.addEventListener('mouseleave', () => {
            button.style.background = '#5778d8';
            button.style.transform = 'translateY(0)';
            button.style.boxShadow = '0 1px 4px rgba(0,0,0,0.3)';
        });

        // Click handler - always opens file selection
        button.addEventListener('click', selectFile);

        document.body.appendChild(button);

        // Set initial text
        updateButtonText();
    }
}

// Check if we need to avoid sidebar collision
function adjustButtonPosition() {
    const button = document.getElementById('fsa-file-selector');
    if (!button) return;

    // Check if sidebar is open (TiddlyWiki specific)
    const sidebar = document.querySelector('.tc-sidebar-scrollable');
    if (sidebar && getComputedStyle(sidebar).display !== 'none') {
        // Sidebar is visible, move button up a bit
        button.style.bottom = '60px';
    } else {
        button.style.bottom = '15px';
    }
}

exports.startup = function() {
    // Only run in browser
    if($tw.browser) {
        console.log('File System Access API Saver starting up');
        console.log('FSA supported:', isFileSystemAccessSupported());
        console.log('Loaded from file:', isLoadedFromFile());
        console.log('Suggested filename:', getSuggestedFilename());

        if (!isFileSystemAccessSupported()) {
            console.log('File System Access API not supported, plugin will not initialize');
            return;
        }

        // Single event listener with capture phase
        document.addEventListener('keydown', handleKeyDown, true);

        // Add file selector button after page loads
        setTimeout(() => {
            addFileSelectorButton();
            adjustButtonPosition();
        }, 1000);

        // Adjust button position on window resize
        window.addEventListener('resize', adjustButtonPosition);

        console.log('Event listeners attached');
        showToast('File Access API ready! Select a file to enable Ctrl+S saving.', true, 3000);
    }
};

})();
